import React from 'react';
import s from './style.module.css';

import ReactLogo from '../../assets/logo.png';


const HeaderBlock = ({title, hideBackground = false}) => {
  const styleCover = hideBackground ? { backgroundImage: 'none'} : {};

  return (
    <>
      <div class={s.cover} style={styleCover}>
        <div class={s.wrap}>
          <h1 class={s.header}>{title}</h1>
          <img src = {ReactLogo} />
          <p class={s.descr}>Воспользуйтесь карточками для запоминания и пополнения активныйх словарных запасов</p>
        </div>
      </div>
    </>
  )
}

export default HeaderBlock;
