import logo from './logo.svg';
import './App.css';
import HeaderBlock from './components/HeaderBlock';
import ContentBlock from './components/ContentBlock';
import FooterBlock from './components/FooterBlock';
import Header from './components/Header';
import Paragraph from './components/Paragraph';
import Card from './components/Card';

/* 
*Вывод всех компонентов
*/

export const wordsList = [
  {
      eng: 'between',
      rus: 'между'
  },
  {
      eng: 'high',
      rus: 'высокий'
  },
  {
      eng: 'really',
      rus: 'действительно'
  },
  {
      eng: 'something',
      rus: 'что-нибудь'
  },
  {
      eng: 'most',
      rus: 'большинство'
  },
  {
      eng: 'another',
      rus: 'другой'
  },
  {
      eng: 'much',
      rus: 'много'
  },
  {
      eng: 'family',
      rus: 'семья'
  },
  {
      eng: 'own',
      rus: 'личный'
  },
  {
      eng: 'out',
      rus: 'из/вне'
  },
  {
      eng: 'leave',
      rus: 'покидать'
  },
];


const App = () => {
  return (
    <>
      <HeaderBlock >
      <Header>
        Время учить слова онлайн
      </Header>
      <Paragraph>
        Используйте карточки для запоминания и пополняйте активный словарный запас.
      </Paragraph>
      </HeaderBlock>
      <div>
        {
          wordsList.map(({eng, rus}) => <Card eng={eng} rus={rus}/>)
        }
      </div>
      <HeaderBlock >
      <Header>
        Еще один заголовок
      </Header>
      <Paragraph>
        Ну здорово же!
      </Paragraph>
      </HeaderBlock>

      <ContentBlock 
      title = 'Учите слова онлайн'
      hideBackground
      content = 'ContentBlock должен содержать заголовок и абзац текста, цвет фона однотонный, например, белый.'
      />

      <FooterBlock 
      title = 'THANKS FOR VISITING'
      copyright = '© 2021 #ReactMarathon.'
      />

    </>
    );
}

export default App;
